import React , {useState} from 'react';
import { Link , Redirect} from 'react-router-dom';
import Base from '../core/Base';

import {signin ,authenticate, isAuthenticated } from "../auth/helper"


const Signin = () =>{

    const [values , setValues] = useState({
        email:"",
        password:"",
        error:"",
        loading:false,
        didRedirect:false
    })

    // destructure 
    const {email , password,error , loading ,didRedirect} = values;
    //getting user from authentication
    const {user} = isAuthenticated();

    //handling on change  values
    const handleChange = name => event =>{

        setValues({...values , error:false , [name] : event.target.value});
    };
//on submit 

    const onSubmit = () => {
        Event.preventDefault();
        setValues({...values , error:false , loading:true});
        signin({email, password})
        .then(data => {
            if(data.error){
                setValues({...values , error:data.error , loading:false});
            }else{
                authenticate(data , () => {
                    setValues({...values , didRedirect:true});
                });
            }
        })
        .catch(console.log("sign-in request failed or database preblem"));
    };

    //jusdging method so  should we have to redirect or not 

    const performRedirect = () => {
        if (didRedirect) {
          if (user && user.role === 1) {
            return <p>redirect to admin</p>;
          } else {
            return <p>redirect to user dashboard</p>;
          }
        }
        if (isAutheticated()) {
          return <Redirect to="/" />;
        }
      };

      const loadingMessage = () => {
        return (
    loading && (
        <div className="alert alert-info">
            <h2>Loading...</h2>
        </div>
    )
  );
};

//error massage after submit

const errorMessage = () => {
    return (
      <div className="row">
        <div className="col-md-6 offset-sm-3 text-left">
          <div
            className="alert alert-danger"
            style={{ display: error ? "" : "none" }}
          >                 
                    {error}
                </div>
            </div>
        </div>
     );
};

const signInForm = () => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                  <form>
                     <div className="form-group">
                      <label className="text-light mt-2">Email</label>
                      <input  
                      onChange={handleChange("email")} 
                      className="form-control" 
                      type="email" value={email}/>
                     </div>

                     <div className="form-group">
                      <label className="text-light mt-2">Password</label>
                      <input onChange={handleChange("password")} className="form-control" type="password" value={password}/>
                     </div>

                     <button onClick={onSubmit} className="btn btn-block btn-success mt-3" style={{width:"100%"}}>Submit</button>

                  </form>

                </div>
            </div>
        )
    }



    return (
        <Base title="sign in page" description ="page for sign-in!">

        {loadingMessage()}
        {errorMessage()}
        {signInForm()}
        {performRedirect()}
    <p className="text-white text-center ">{JSON.stringify(values)}</p>
        </Base>
    );

};

export default Signin;
